import 'bootstrap/dist/css/bootstrap.min.css';



const AppContent = () => {
  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col md={8}>
          <h1 className="text-center mb-4">ðŸ’± Conversor de Moedas</h1>
