            Converta moedas usando React useReducer + Context API
          </p>

          <CurrencyConverter />
          <CurrencyInfo />
        </Col>
      </Row>
    </Container>
  );
};

function App() {
  return (
    <CurrencyProvider>
      <AppContent />
    </CurrencyProvider>
  );
}

export default App;
