import React from 'react';
import { Container, Row, Col, Nav } from 'react-bootstrap';
import { useState } from 'react';
import CurrencyConverter from './CurrencyConverter';
import CurrencyInfo from './CurrencyInfo';
import SugerirMoeda from './SugerirMoeda';
import Mural from './Mural';
import LogoutButton from './LogoutButton';
import LoginButton from './LoginButton';
import './Dashboard.css';

import { CurrencyProvider } from '../contexts/CurrencyContext';

export default function Dashboard() {
  const [activeTab, setActiveTab] = useState('converter');

  return (
    <CurrencyProvider>
      <Container fluid className="dashboard-container py-4">
      {/* Header com bot√µes */}
      <Row className="mb-4 align-items-center">
        <Col md={8}>
          <h1 className="text-center mb-0">üí± Conversor de Moedas</h1>
        </Col>
        <Col md={4} className="text-end">
          <LogoutButton />
        </Col>
      </Row>

      {/* Abas de navega√ß√£o */}
      <Row className="mb-4">
        <Col md={12}>
          <Nav className="nav-tabs" activeKey={activeTab} onSelect={(k) => setActiveTab(k)}>
            <Nav.Item>
              <Nav.Link eventKey="converter" className="nav-link-custom">
                üîÑ Conversor
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="sugerir" className="nav-link-custom">
                üí° Sugerir Moeda
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="mural" className="nav-link-custom">
                üìù Mural de Sugest√µes
              </Nav.Link>
            </Nav.Item>
          </Nav>
        </Col>
      </Row>

      {/* Conte√∫do das abas */}
      <Row className="justify-content-center">
        <Col md={8}>
          {/* Tab: Conversor */}
          {activeTab === 'converter' && (
            <div className="tab-content">
              <p className="text-center text-muted mb-5">
                Converta moedas usando React useReducer + Context API
              </p>
              <CurrencyConverter />
              <CurrencyInfo />
            </div>
          )}

          {/* Tab: Sugerir Moeda */}
          {activeTab === 'sugerir' && (
            <div className="tab-content">
              <SugerirMoeda />
            </div>
          )}

          {/* Tab: Mural */}
          {activeTab === 'mural' && (
            <div className="tab-content">
              <Mural />
            </div>
          )}
        </Col>
      </Row>
    </Container>
    </CurrencyProvider>
  );
}
